import { useEffect, useState } from "react";
import "./App.css"

function App() {
  const [list, setList] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    console.log(list)
  },[list])

  const insertData = () => {
    setText("");
    setList([
      ...list,
      {
        index: list.length,
        text: text,
        status: false
      }
    ])
  }

  const deleletLine = (index) => {
    const getList = list.filter((it)=> it.index !== index)
    setList(getList);
  }

  const successLune = (index) => {
    const getList = [...list];
    getList[index].status = true;
    setList(getList);
  }

  return (
    <div className="App">
      <input type={"text"} value={text} onChange={(e)=> {
        setText(e.target.value)
      }}/>
      <input type={"button"} value={"버튼"} onClick={insertData}/>
      <div className="contanier">
        <ol>
          {
            list.map(it => 
              (
                <li key={it.index} className={it.status === true ? "line" : ""}>{it.text} &nbsp;&nbsp;&nbsp;
                <button onClick={() => {
                  deleletLine(it.index);
                }}>삭제</button>
                <button onClick={() => {
                  successLune(it.index);
                }}>완료</button>
                </li>
              )
            )
          }
        </ol>
      </div>

    </div>
  );
}

export default App;
