import { useEffect, useState } from "react";

function App() {
  const [list, setList] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    console.log(list)
  },[list])

  const insertData = () => {
    setText("");
    setList([
      ...list,
      {
        index: list.length,
        text: text,
        status: false
      }
    ])
  }

  return (
    <div className="App">
      <input type={"text"} value={text} onChange={(e)=> {
        setText(e.target.value)
      }}/>
      <input type={"button"} value={"버튼"} onClick={insertData}/>
      <div className="contanier">
        <ol>
          {
            list.map(it => 
              (<>
                <li key={it.index}>{it.text} &nbsp;&nbsp;&nbsp;
                <button>삭제</button>
                <button>완료</button>
                </li>
              </>)
            )
          }
        </ol>
      </div>

    </div>
  );
}

export default App;
